import React from "react";
import MonthSpendCard from '../../components/MonthSpendCard.js/MonthSpendCard'
import SpendInput from '../../containers/SpendInput/SpendInput'

// Static design. Multiple tables 

const Dashboard = () => {

    const dbObject = {
        budgets: {
            category: {
                rent: {
                    name: "Rent",
                    percentage: 30,
                    monthly: 700,
                    annual: 1000,
                    category: "Essential"
                },
                groceries: {
                    name: "Groceries",
                    percentage: 8,
                    monthly: 150,
                    annual: 1000,
                    category: "Essential"
                },
                transport: {
                    name: "Transport",
                    percentage: 4,
                    monthly: 90,
                    annual: 1000,
                    category: "Essential"
                },
                entertainment: {
                    name: "Entertainment",
                    percentage: 12,
                    monthly: 310,
                    annual: 1000,
                    category: "Luxury"
                }
            },
        },

        spending: {
            month: {
                june: {
                    rent: {
                        name: "Rent",
                        spent: 600,
                        category: "Essential"
                    },
                    groceries: {
                        name: "Groceries",
                        spent: 150,
                        category: "Essential"
                    },
                    transport: {
                        name: "Transport",
                        spent: 101,
                        category: "Essential"
                    },
                    entertainment: {
                        name: "Entertainment",

                        spent: 300,
                        category: "Luxury"
                    },

                },
                july: {

                    rent: {
                        name: "Rent",
                        spent: 78,
                        category: "Essential"
                    },
                    groceries: {
                        name: "Groceries",
                        spent: 733,
                        category: "Essential"
                    },
                    transport: {
                        name: "Transport",
                        spent: 765,
                        category: "Essential"
                    },
                    entertainment: {
                        name: "Entertainment",
                        percentage: 12,
                        spent: 765,
                        category: "Luxury"
                    },

                },
                august: {
                    rent: {
                        name: "Rent",
                        spent: 800,
                        category: "Essential"
                    },
                    groceries: {
                        name: "Groceries",
                        spent: 876,
                        category: "Essential"
                    },
                    transport: {
                        name: "Transport",
                        spent: 844,
                        category: "Essential"
                    },
                    entertainment: {
                        name: "Entertainment",
                        percentage: 12,
                        spent: 843,
                        category: "Luxury"
                    },

                },
            },
        },
    }

    const months = Object.keys(dbObject.spending.month).map((monthKey) => {
        return <MonthSpendCard
            key={monthKey}
            spending={dbObject.spending.month[monthKey]}
            month={monthKey}
        />;
    });

    return (
        <>
            <div className="bg-white px-8 flex h-20 border-b border-gray-300">
                <h2 className="font-bold text-2xl block my-auto">Overview</h2>
            </div>
            <div className="max-w-7xl mx-auto">
                <div className="flex flex-col">
                    <div className="bg-white">
                        <SpendInput />
                    </div>
                </div>
            </div>
            <div className="max-w-7xl mx-auto">
                <div className="flex flex-col">
                    <div className="bg-white">{months}</div>
                </div>
            </div>
        </>
    );
};

export default Dashboard;
