import React from "react";
const SpendInputRow = (props) => {

    return (
        < React.Fragment >

            <tr className=" h-16 hover:bg-gray-100 transition-all ease-linear duration-200 cursor-pointer text-sm leading-5 text-gray-900 font-semibold" >
                <td className=" capitalize w-1/2 whitespace-no-wrap border-b border-gray-200">
                    {props.cat}
                </td>

                <td className="w-1/2 whitespace-no-wrap border-b border-gray-200 ">
                    <input className="text-center text-sm leading-5 text-gray-900 font-semibold appearance-none bg-transparent border-none w-full mr-3 py-1 px-2 leading-tight focus:outline-none "
                        type="text"
                        placeholder="0"
                        aria-label="spending"
                        onChange={event => {
                            props.enteredSpendHandler(event.target.value, props.cat)
                        }}
                    >
                    </input>
                </td>
            </tr>
        </React.Fragment >
    );
}

export default SpendInputRow;
