import React from "react";
import PlannerTableRow from '../../components/PlannerTableRow/PlannerTableRow'

// Static design. Multiple tables 

const Dashboard = () => {

    // Budgets and spending will be stored in DB

    const dbObject = {
        budgets: {
            category: {
                rent: {
                    name: "Rent",
                    percentage: 30,
                    monthly: 700,
                    annual: 1000,
                    category: "Essential"
                },
                groceries: {
                    name: "Groceries",
                    percentage: 8,
                    monthly: 150,
                    annual: 1000,
                    category: "Essential"
                },
                transport: {
                    name: "Transport",
                    percentage: 4,
                    monthly: 90,
                    annual: 1000,
                    category: "Essential"
                },
                entertainment: {
                    name: "Entertainment",
                    percentage: 12,
                    monthly: 310,
                    annual: 1000,
                    category: "Luxury"
                }
            },
        },

        spending: {
            june: {

                rent: {
                    name: "Rent",
                    spent: 645,
                    category: "Essential"
                },
                groceries: {
                    name: "Groceries",
                    spent: 150,
                    category: "Essential"
                },
                transport: {
                    name: "Transport",
                    spent: 101,
                    category: "Essential"
                },
                entertainment: {
                    name: "Entertainment",

                    spent: 300,
                    category: "Luxury"
                },

            },
            july: {

                rent: {
                    name: "Rent",
                    spent: 645,
                    category: "Essential"
                },
                groceries: {
                    name: "Groceries",
                    spent: 150,
                    category: "Essential"
                },
                transport: {
                    name: "Transport",
                    spent: 101,
                    category: "Essential"
                },
                entertainment: {
                    name: "Entertainment",
                    percentage: 12,
                    spent: 300,
                    category: "Luxury"
                },

            },
            august: {

                rent: {
                    name: "Rent",
                    spent: 645,
                    category: "Essential"
                },
                groceries: {
                    name: "Groceries",
                    spent: 150,
                    category: "Essential"
                },
                transport: {
                    name: "Transport",
                    spent: 101,
                    category: "Essential"
                },
                entertainment: {
                    name: "Entertainment",
                    percentage: 12,
                    spent: 300,
                    category: "Luxury"
                },

            },
        },
    }

    const tableRows = Object.keys(dbObject.spending).map((monthKey) => {
        const cat = Object.keys(dbObject.spending[monthKey])
        console.log(cat[0])
        return <PlannerTableRow
            key={monthKey} spending={dbObject.spending[monthKey][cat[0]]}
        />;
    });


    return (
        <>
            <div className="bg-white px-8 flex h-20 border-b border-gray-300">
                <h2 className="font-bold text-2xl block my-auto">Overview</h2>
            </div>

            <div className="max-w-7xl mx-auto">
                <div className="flex flex-col">
                    <div className="align-middle inline-block min-w-full bg-gray-100 border-b border-gray-200">
                        <table className="min-w-full max-w-full">
                            <thead>
                                <tr>
                                    <th className="w-1/2 px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Category
                      </th>
                                    <th className="px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Spent
                      </th>

                                </tr>
                            </thead>
                            <tbody className="bg-white">{tableRows}</tbody>
                        </table>
                    </div>
                </div>

            </div>

            <div className="max-w-7xl mx-auto">
                <div className="flex flex-col">
                    <div className="align-middle inline-block min-w-full bg-gray-100 border-b border-gray-200">
                        <table className="min-w-full max-w-full">
                            <thead>
                                <tr>
                                    <th className="w-1/2 px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Category
                      </th>
                                    <th className="px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Spent
                      </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white">{tableRows}</tbody>
                        </table>
                    </div>
                </div>

            </div>

        </>
    );
};

export default Dashboard;
