import React, { useState } from "react";
import TableRow from "../../components/TableRow/TableRow";

//To Do: 
// Two way binding with input and state (income)
// When input changes, change state <-- understand how it update objects as state

// Reducer/Handler



const Dashboard = () => {

    const [enteredIncome, setEnteredIncome] = useState('10000');
    const [monthlyRent, SetMonthlyRent] = useState('');
    const [monthlyGroceries, SetMonthlyGroceries] = useState('');
    const [monthlyTransport, SetMonthlyTransport] = useState('');
    const [monthlyEntertainment, SetMonthlyEntertainment] = useState('');

    const budgets = {
        category: {
            rent: {
                name: "Rent",
                percentage: 30,
                monthly: monthlyRent,
                category: "Essential"
            },
            groceries: {
                name: "Groceries",
                percentage: 8,
                monthly: monthlyGroceries,
                category: "Essential"
            },
            transport: {
                name: "Transport",
                percentage: 4,
                monthly: monthlyTransport,
                category: "Essential"
            },
            entertainment: {
                name: "Entertainment",
                percentage: 12,
                monthly: monthlyEntertainment,
                category: "Luxury"
            }
        },
    };

    const tableRows = Object.keys(budgets.category).map((catKey) => {
        return <TableRow key={catKey} budget={budgets.category[catKey]} />;
    });

    console.log(`Budgets after object ${budgets}`)

    const budgetReducer = (income, budgets, name) => {
        Object.keys(budgets.category).map((catKey) => {
            console.log('Percent')
            console.log(budgets.category[catKey].percentage)
            console.log('Income')
            console.log(income)
            const newAnnual = (income / 100 * budgets.category[catKey].percentage);
            const newMonthly = newAnnual / 12;
            switch (budgets.category[catKey].name) {
                case 'Rent':
                    SetMonthlyRent(newAnnual)
                    break;
                case 'Groceries':
                    SetMonthlyGroceries(newAnnual)
                    break;
                case 'Transport':
                    SetMonthlyTransport(newAnnual)
                    break;
                case 'Entertainment':
                    SetMonthlyEntertainment(newAnnual)
                    break;
                default:
                    console.log('Error')
            }
        })
    };

    return (
        <>
            <div className="bg-white px-8 flex h-20 border-b border-gray-300">
                <h2 className="font-bold text-2xl block my-auto">Budget planner</h2>
            </div>

            <form class="w-full max-w-sm" >
                <div class="flex items-center border-b border-teal-500 py-2">
                    <input class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
                        type="text"
                        placeholder="Â£25,000"
                        aria-label="Income"
                        value={enteredIncome}
                        onChange={event => {
                            setEnteredIncome(event.target.value)
                            budgetReducer(parseInt(event ? event.target.value : enteredIncome), budgets)
                        }}
                    >
                    </input>
                </div>
            </form>

            <div className="max-w-7xl mx-auto">
                <div className="flex flex-col">
                    <div className="align-middle inline-block min-w-full bg-gray-100 border-b border-gray-200">
                        <table className="min-w-full max-w-full">
                            <thead>
                                <tr>
                                    <th className="w-1/2 px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Category
                      </th>
                                    <th className="px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Percentage
                      </th>
                                    <th className="md:w-48 px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Annual
                      </th>
                                    <th className="md:w-48 px-8 py-4 border-b border-gray-200 bg-gray-50 text-left text-sm leading-4 font-medium text-gray-500 uppercase tracking-wider">
                                        Category
                      </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white">{tableRows}</tbody>
                        </table>
                    </div>
                    {/* {loading && <Loading />}
              {!loading && !projectRows[0] && (
                <div className="text-gray-500 p-4 text-center">
                  No projects created
                </div>
              )} */}
                </div>
            </div>
        </>
    );
};

export default Dashboard;
